definition.script:
node {
  stage('Create POM') {
    writeFile file: 'pom.xml', text: '<project><modelVersion>4.0.0</modelVersion><groupId>org.sonarsource.test</groupId><artifactId>org.sonarsource.test</artifactId><version>1-SNAPSHOT</version><packaging>pom</packaging></project>'
  }
  stage('Analyse'){
    withSonarQubeEnv {
      withMaven {
        sh "mvn $env.SONAR_MAVEN_GOAL -Dsonar.host.url=$env.SONAR_HOST_URL"
      }
    }
  }
}

def withMaven(def body) {
  def javaHome = tool name: 'Java 8', type: 'hudson.model.JDK'
  def mvnHome = tool name: 'Maven 3.2.x', type: 'hudson.tasks.Maven$MavenInstallation'
  withEnv(["JAVA_HOME=${javaHome}", "PATH+MAVEN=${mvnHome}/bin"]) {
    body.call()
  }
}
